contracts/LiquidityAggregator.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

interface IERC20Extended is IERC20 {
    function decimals() external view returns (uint8);
}

interface IRATStakingPool {
    function addYield(uint256 amount) external;
}

interface IPancakeRouter {
    function swapExactTokensForTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);
    
    function getAmountsOut(uint256 amountIn, address[] calldata path)
        external view returns (uint256[] memory amounts);
}

contract LiquidityAggregator is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20Extended;
    
    struct DEXConfig {
        address router;
        bool isActive;
        uint256 priority; // Lower number = higher priority
        string name;
    }
    
    struct SwapResult {
        uint256 amountOut;
        address dexUsed;
        string dexName;
        uint256 gasUsed;
    }
    
    // State variables
    mapping(address => DEXConfig) public dexConfigs;
    address[] public activeDEXes;
    
    IERC20Extended public immutable USDT;
    address public ratStakingPool;
    
    uint256 public totalUSDTAggregated;
    uint256 public totalSwapsExecuted;
    uint256 public slippageTolerance = 300; // 3% in basis points
    
    // Events
    event LiquidityAggregated(
        address indexed fromToken,
        uint256 amountIn,
        uint256 usdtOut,
        address dexUsed,
        string dexName
    );
    
    event DEXConfigured(
        address indexed router,
        bool isActive,
        uint256 priority,
        string name
    );
    
    event SlippageToleranceUpdated(uint256 oldTolerance, uint256 newTolerance);
    event StakingPoolUpdated(address oldPool, address newPool);
    
    constructor(
        address _usdtAddress,
        address initialOwner
    ) Ownable(initialOwner) {
        require(_usdtAddress != address(0), "Invalid USDT address");
        USDT = IERC20Extended(_usdtAddress);
    }
    
    /**
     * @dev Configure a DEX router
     * @param router Address of the DEX router
     * @param isActive Whether the DEX is active
     * @param priority Priority level (lower = higher priority)
     * @param name Name of the DEX
     */
    function configureDEX(
        address router,
        bool isActive,
        uint256 priority,
        string memory name
    ) external onlyOwner {
        require(router != address(0), "Invalid router address");
        
        dexConfigs[router] = DEXConfig({
            router: router,
            isActive: isActive,
            priority: priority,
            name: name
        });
        
        // Add to active DEXes if not already present and is active
        if (isActive && !_isInActiveDEXes(router)) {
            activeDEXes.push(router);
        } else if (!isActive) {
            _removeFromActiveDEXes(router);
        }
        
        emit DEXConfigured(router, isActive, priority, name);
    }
    
    /**
     * @dev Set the RAT staking pool address
     * @param _ratStakingPool Address of the RAT staking pool
     */
    function setRATStakingPool(address _ratStakingPool) external onlyOwner {
        require(_ratStakingPool != address(0), "Invalid staking pool address");
        
        address oldPool = ratStakingPool;
        ratStakingPool = _ratStakingPool;
        
        emit StakingPoolUpdated(oldPool, _ratStakingPool);
    }
    
    /**
     * @dev Aggregate liquidity by swapping tokens to USDT
     * @param fromToken Token to swap from
     * @param amountIn Amount of tokens to swap
     * @param amountOutMin Minimum amount of USDT expected
     * @return swapResult Details of the swap execution
     */
    function aggregateLiquidity(
        address fromToken,
        uint256 amountIn,
        uint256 amountOutMin
    ) external nonReentrant returns (SwapResult memory swapResult) {
        require(fromToken != address(0), "Invalid token address");
        require(amountIn > 0, "Amount must be greater than 0");
        require(fromToken != address(USDT), "Cannot swap USDT to USDT");
        
        IERC20Extended(fromToken).safeTransferFrom(msg.sender, address(this), amountIn);
        
        // Find best DEX for the swap
        (address bestDEX, uint256 bestAmountOut, string memory bestDEXName) = _findBestDEX(fromToken, amountIn);
        require(bestDEX != address(0), "No suitable DEX found");
        require(bestAmountOut >= amountOutMin, "Insufficient output amount");
        
        // Execute the swap
        uint256 usdtBalanceBefore = USDT.balanceOf(address(this));
        uint256 gasStart = gasleft();
        
        _executeSwap(fromToken, amountIn, bestAmountOut, bestDEX);
        
        uint256 gasUsed = gasStart - gasleft();
        uint256 usdtReceived = USDT.balanceOf(address(this)) - usdtBalanceBefore;
        
        // Update statistics
        totalUSDTAggregated += usdtReceived;
        totalSwapsExecuted++;
        
        // Distribute yield to staking pool (10% of swap amount)
        if (ratStakingPool != address(0) && usdtReceived > 0) {
            uint256 yieldAmount = usdtReceived / 10; // 10% yield
            if (yieldAmount > 0) {
                USDT.forceApprove(address(ratStakingPool), yieldAmount);
                IRATStakingPool(ratStakingPool).addYield(yieldAmount);
            }
        }
        
        // Transfer remaining USDT to the caller
        uint256 remainingUSDT = USDT.balanceOf(address(this));
        if (remainingUSDT > 0) {
            USDT.safeTransfer(msg.sender, remainingUSDT);
        }
        
        swapResult = SwapResult({
            amountOut: usdtReceived,
            dexUsed: bestDEX,
            dexName: bestDEXName,
            gasUsed: gasUsed
        });
        
        emit LiquidityAggregated(fromToken, amountIn, usdtReceived, bestDEX, bestDEXName);
    }
    
    /**
     * @dev Get the best quote for swapping tokens to USDT
     * @param fromToken Token to swap from
     * @param amountIn Amount of tokens to swap
     * @return bestDEX Address of the best DEX
     * @return bestAmountOut Best amount out from the best DEX
     * @return bestDEXName Name of the best DEX
     */
    function getBestQuote(
        address fromToken,
        uint256 amountIn
    ) external view returns (
        address bestDEX,
        uint256 bestAmountOut,
        string memory bestDEXName
    ) {
        return _findBestDEX(fromToken, amountIn);
    }
    
    /**
     * @dev Set slippage tolerance
     * @param _slippageTolerance New slippage tolerance in basis points
     */
    function setSlippageTolerance(uint256 _slippageTolerance) external onlyOwner {
        require(_slippageTolerance <= 1000, "Slippage tolerance too high"); // Max 10%
        
        uint256 oldTolerance = slippageTolerance;
        slippageTolerance = _slippageTolerance;
        
        emit SlippageToleranceUpdated(oldTolerance, _slippageTolerance);
    }
    
    /**
     * @dev Get all active DEXes
     */
    function getActiveDEXes() external view returns (address[] memory) {
        return activeDEXes;
    }
    
    /**
     * @dev Get aggregation statistics
     */
    function getStats() external view returns (
        uint256 totalAggregated,
        uint256 totalSwaps,
        uint256 activeDEXCount
    ) {
        return (totalUSDTAggregated, totalSwapsExecuted, activeDEXes.length);
    }
    
    /**
     * @dev Emergency withdrawal function (admin only)
     * @param token Token to withdraw
     * @param amount Amount to withdraw
     */
    function emergencyWithdraw(address token, uint256 amount) external onlyOwner {
        IERC20Extended(token).safeTransfer(owner(), amount);
    }
    
    // Internal functions
    
    function _findBestDEX(
        address fromToken,
        uint256 amountIn
    ) internal view returns (
        address bestDEX,
        uint256 bestAmountOut,
        string memory bestDEXName
    ) {
        for (uint256 i = 0; i < activeDEXes.length; i++) {
            address dexRouter = activeDEXes[i];
            DEXConfig memory config = dexConfigs[dexRouter];
            
            if (!config.isActive) continue;
            
            try this._getQuoteFromDEX(dexRouter, fromToken, amountIn) returns (uint256 amountOut) {
                if (amountOut > bestAmountOut) {
                    bestDEX = dexRouter;
                    bestAmountOut = amountOut;
                    bestDEXName = config.name;
                }
            } catch {
                // Skip this DEX if quote fails
                continue;
            }
        }
    }
    
    function _getQuoteFromDEX(
        address router,
        address fromToken,
        uint256 amountIn
    ) external view returns (uint256 amountOut) {
        address[] memory path = new address[](2);
        path[0] = fromToken;
        path[1] = address(USDT);
        
        uint256[] memory amounts = IPancakeRouter(router).getAmountsOut(amountIn, path);
        return amounts[1];
    }
    
    function _executeSwap(
        address fromToken,
        uint256 amountIn,
        uint256 amountOutMin,
        address router
    ) internal {
        IERC20Extended(fromToken).forceApprove(router, amountIn);
        
        address[] memory path = new address[](2);
        path[0] = fromToken;
        path[1] = address(USDT);
        
        // Apply slippage tolerance
        uint256 minAmountOut = (amountOutMin * (10000 - slippageTolerance)) / 10000;
        
        IPancakeRouter(router).swapExactTokensForTokens(
            amountIn,
            minAmountOut,
            path,
            address(this),
            block.timestamp + 300 // 5 minutes deadline
        );
    }
    
    function _isInActiveDEXes(address router) internal view returns (bool) {
        for (uint256 i = 0; i < activeDEXes.length; i++) {
            if (activeDEXes[i] == router) {
                return true;
            }
        }
        return false;
    }
    
    function _removeFromActiveDEXes(address router) internal {
        for (uint256 i = 0; i < activeDEXes.length; i++) {
            if (activeDEXes[i] == router) {
                activeDEXes[i] = activeDEXes[activeDEXes.length - 1];
                activeDEXes.pop();
                break;
            }
        }
    }
}

contracts/PledgeManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "./interfaces/IPriceOracle.sol";

contract PledgeManager is Ownable, ReentrancyGuard, Pausable {
    
    struct AssetPledge {
        address pledger;
        string assetType;
        uint256 assetValue;
        string ipfsHash;
        uint256 timestamp;
        PledgeStatus status;
        uint256 ratTokensMinted;
        string verificationNotes;
    }
    
    enum PledgeStatus {
        Pending,
        Verified,
        Rejected,
        Liquidated
    }
    
    // State variables
    mapping(uint256 => AssetPledge) public pledges;
    mapping(address => uint256[]) public userPledges;
    uint256 public nextPledgeId = 1;
    uint256 public totalActiveValue;
    
    IPriceOracle public priceOracle;
    address public ratTokenAddress;
    
    // Events
    event AssetPledged(
        uint256 indexed pledgeId,
        address indexed pledger,
        string assetType,
        uint256 assetValue,
        string ipfsHash
    );
    
    event PledgeVerified(
        uint256 indexed pledgeId,
        uint256 ratTokensMinted,
        string verificationNotes
    );
    
    event PledgeRejected(
        uint256 indexed pledgeId,
        string reason
    );
    
    event PledgeLiquidated(
        uint256 indexed pledgeId,
        uint256 recoveredValue
    );
    
    constructor(address initialOwner) Ownable(initialOwner) {}
    
    /**
     * @dev Submit a new asset pledge
     * @param assetType Type of asset being pledged
     * @param assetValue Declared value of the asset
     * @param ipfsHash IPFS hash of asset documentation
     */
    function submitPledge(
        string memory assetType,
        uint256 assetValue,
        string memory ipfsHash
    ) external whenNotPaused nonReentrant {
        require(bytes(assetType).length > 0, "Asset type cannot be empty");
        require(assetValue > 0, "Asset value must be greater than 0");
        require(bytes(ipfsHash).length > 0, "IPFS hash cannot be empty");
        
        uint256 pledgeId = nextPledgeId++;
        
        pledges[pledgeId] = AssetPledge({
            pledger: msg.sender,
            assetType: assetType,
            assetValue: assetValue,
            ipfsHash: ipfsHash,
            timestamp: block.timestamp,
            status: PledgeStatus.Pending,
            ratTokensMinted: 0,
            verificationNotes: ""
        });
        
        userPledges[msg.sender].push(pledgeId);
        
        emit AssetPledged(pledgeId, msg.sender, assetType, assetValue, ipfsHash);
    }
    
    /**
     * @dev Verify and approve an asset pledge (admin only)
     * @param pledgeId ID of the pledge to verify
     * @param ratTokensToMint Amount of RAT tokens to mint
     * @param verificationNotes Notes from the verification process
     */
    function verifyPledge(
        uint256 pledgeId,
        uint256 ratTokensToMint,
        string memory verificationNotes
    ) external onlyOwner {
        require(pledges[pledgeId].pledger != address(0), "Pledge does not exist");
        require(pledges[pledgeId].status == PledgeStatus.Pending, "Pledge not pending");
        require(ratTokensToMint > 0, "Must mint at least 1 token");
        
        pledges[pledgeId].status = PledgeStatus.Verified;
        pledges[pledgeId].ratTokensMinted = ratTokensToMint;
        pledges[pledgeId].verificationNotes = verificationNotes;
        
        totalActiveValue += pledges[pledgeId].assetValue;
        
        // Mint RAT tokens to the pledger
        // Note: This requires the RATToken contract to be set and this contract to have minting rights
        if (ratTokenAddress != address(0)) {
            // Call mint function on RAT token contract
            (bool success, ) = ratTokenAddress.call(
                abi.encodeWithSignature(
                    "mint(address,uint256,string)",
                    pledges[pledgeId].pledger,
                    ratTokensToMint,
                    string(abi.encodePacked("Asset pledge verification #", toString(pledgeId)))
                )
            );
            require(success, "Failed to mint RAT tokens");
        }
        
        emit PledgeVerified(pledgeId, ratTokensToMint, verificationNotes);
    }
    
    /**
     * @dev Reject an asset pledge (admin only)
     * @param pledgeId ID of the pledge to reject
     * @param reason Reason for rejection
     */
    function rejectPledge(
        uint256 pledgeId,
        string memory reason
    ) external onlyOwner {
        require(pledges[pledgeId].pledger != address(0), "Pledge does not exist");
        require(pledges[pledgeId].status == PledgeStatus.Pending, "Pledge not pending");
        
        pledges[pledgeId].status = PledgeStatus.Rejected;
        pledges[pledgeId].verificationNotes = reason;
        
        emit PledgeRejected(pledgeId, reason);
    }
    
    /**
     * @dev Mark a pledge as liquidated (admin only)
     * @param pledgeId ID of the pledge to liquidate
     * @param recoveredValue Value recovered from liquidation
     */
    function liquidatePledge(
        uint256 pledgeId,
        uint256 recoveredValue
    ) external onlyOwner {
        require(pledges[pledgeId].pledger != address(0), "Pledge does not exist");
        require(pledges[pledgeId].status == PledgeStatus.Verified, "Pledge not verified");
        
        pledges[pledgeId].status = PledgeStatus.Liquidated;
        totalActiveValue -= pledges[pledgeId].assetValue;
        
        emit PledgeLiquidated(pledgeId, recoveredValue);
    }
    
    /**
     * @dev Set the RAT token contract address (admin only)
     * @param _ratTokenAddress Address of the RAT token contract
     */
    function setRATTokenAddress(address _ratTokenAddress) external onlyOwner {
        require(_ratTokenAddress != address(0), "Invalid token address");
        ratTokenAddress = _ratTokenAddress;
    }
    
    /**
     * @dev Set the price oracle contract (admin only)
     * @param _priceOracle Address of the price oracle contract
     */
    function setPriceOracle(address _priceOracle) external onlyOwner {
        require(_priceOracle != address(0), "Invalid oracle address");
        priceOracle = IPriceOracle(_priceOracle);
    }
    
    /**
     * @dev Get pledge details
     * @param pledgeId ID of the pledge
     */
    function getPledge(uint256 pledgeId) external view returns (AssetPledge memory) {
        require(pledges[pledgeId].pledger != address(0), "Pledge does not exist");
        return pledges[pledgeId];
    }
    
    /**
     * @dev Get all pledge IDs for a user
     * @param user Address of the user
     */
    function getUserPledges(address user) external view returns (uint256[] memory) {
        return userPledges[user];
    }
    
    /**
     * @dev Get total number of pledges
     */
    function getTotalPledges() external view returns (uint256) {
        return nextPledgeId - 1;
    }
    
    /**
     * @dev Pause the contract (admin only)
     */
    function pause() external onlyOwner {
        _pause();
    }
    
    /**
     * @dev Unpause the contract (admin only)
     */
    function unpause() external onlyOwner {
        _unpause();
    }
    
    /**
     * @dev Helper function to convert uint to string
     */
    function toString(uint256 value) internal pure returns (string memory) {
        if (value == 0) {
            return "0";
        }
        uint256 temp = value;
        uint256 digits;
        while (temp != 0) {
            digits++;
            temp /= 10;
        }
        bytes memory buffer = new bytes(digits);
        while (value != 0) {
            digits -= 1;
            buffer[digits] = bytes1(uint8(48 + uint256(value % 10)));
            value /= 10;
        }
        return string(buffer);
    }
}

contracts/PriceOracle.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./interfaces/IPriceOracle.sol";

contract PriceOracle is IPriceOracle, Ownable {
    
    struct PriceData {
        uint256 price;
        uint256 timestamp;
        bool isActive;
    }
    
    mapping(string => PriceData) private assetPrices;
    mapping(address => bool) public authorizedUpdaters;
    
    uint256 public constant PRICE_STALENESS_THRESHOLD = 24 hours;
    
    event PriceUpdated(string indexed asset, uint256 price, uint256 timestamp);
    event UpdaterAuthorized(address indexed updater, bool authorized);
    
    modifier onlyAuthorized() {
        require(authorizedUpdaters[msg.sender] || msg.sender == owner(), "Not authorized");
        _;
    }
    
    constructor(address initialOwner) Ownable(initialOwner) {
        authorizedUpdaters[initialOwner] = true;
    }
    
    function updatePrice(string memory asset, uint256 price) external onlyAuthorized {
        require(bytes(asset).length > 0, "Asset cannot be empty");
        require(price > 0, "Price must be greater than 0");
        
        assetPrices[asset] = PriceData({
            price: price,
            timestamp: block.timestamp,
            isActive: true
        });
        
        emit PriceUpdated(asset, price, block.timestamp);
    }
    
    function getPrice(string memory asset) external view override returns (uint256 price) {
        PriceData memory priceData = assetPrices[asset];
        require(priceData.isActive, "Asset price not available");
        require(
            block.timestamp - priceData.timestamp <= PRICE_STALENESS_THRESHOLD,
            "Price data is stale"
        );
        
        return priceData.price;
    }
    
    function isPriceAvailable(string memory asset) external view override returns (
        bool exists,
        bool isFresh
    ) {
        PriceData memory priceData = assetPrices[asset];
        exists = priceData.isActive;
        isFresh = exists && (block.timestamp - priceData.timestamp <= PRICE_STALENESS_THRESHOLD);
        
        return (exists, isFresh);
    }
    
    function setUpdaterAuthorization(address updater, bool authorized) external onlyOwner {
        require(updater != address(0), "Invalid updater address");
        authorizedUpdaters[updater] = authorized;
        emit UpdaterAuthorized(updater, authorized);
    }
}

contracts/RATStakingPool.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

interface IERC20Extended is IERC20 {
    function decimals() external view returns (uint8);
}

contract RATStakingPool is Ownable, ReentrancyGuard, Pausable {
    using SafeERC20 for IERC20Extended;
    
    struct StakeInfo {
        uint256 amount;
        uint256 stakingTime;
        uint256 lastRewardClaim;
        uint256 accumulatedRewards;
    }
    
    struct PoolInfo {
        uint256 totalStaked;
        uint256 totalRewards;
        uint256 rewardPerTokenStored;
        uint256 lastUpdateTime;
        uint256 rewardRate; // Rewards per second
    }
    
    // State variables
    IERC20Extended public immutable ratToken;
    IERC20Extended public immutable rewardToken; // USDT
    
    PoolInfo public poolInfo;
    mapping(address => StakeInfo) public stakes;
    mapping(address => uint256) public userRewardPerTokenPaid;
    
    uint256 public constant MIN_STAKE_AMOUNT = 100 * 10**18; // 100 RAT tokens
    uint256 public constant MIN_STAKE_DURATION = 7 days;
    uint256 public constant EARLY_UNSTAKE_PENALTY = 1000; // 10% in basis points
    
    // Events
    event Staked(address indexed user, uint256 amount, uint256 timestamp);
    event Unstaked(address indexed user, uint256 amount, uint256 penalty);
    event RewardsClaimed(address indexed user, uint256 amount);
    event YieldAdded(uint256 amount, address indexed contributor);
    event RewardRateUpdated(uint256 newRate);
    
    constructor(
        address _ratToken,
        address _rewardToken,
        address initialOwner
    ) Ownable(initialOwner) {
        require(_ratToken != address(0), "Invalid RAT token address");
        require(_rewardToken != address(0), "Invalid reward token address");
        
        ratToken = IERC20Extended(_ratToken);
        rewardToken = IERC20Extended(_rewardToken);
        
        poolInfo.lastUpdateTime = block.timestamp;
        poolInfo.rewardRate = 1 * 10**18; // 1 USDT per second initially
    }
    
    modifier updateReward(address account) {
        poolInfo.rewardPerTokenStored = rewardPerToken();
        poolInfo.lastUpdateTime = block.timestamp;
        
        if (account != address(0)) {
            stakes[account].accumulatedRewards = earned(account);
            userRewardPerTokenPaid[account] = poolInfo.rewardPerTokenStored;
        }
        _;
    }
    
    /**
     * @dev Stake RAT tokens
     * @param amount Amount of RAT tokens to stake
     */
    function stake(uint256 amount) external whenNotPaused nonReentrant updateReward(msg.sender) {
        require(amount >= MIN_STAKE_AMOUNT, "Amount below minimum stake");
        
        ratToken.safeTransferFrom(msg.sender, address(this), amount);
        
        stakes[msg.sender].amount += amount;
        stakes[msg.sender].stakingTime = block.timestamp;
        stakes[msg.sender].lastRewardClaim = block.timestamp;
        
        poolInfo.totalStaked += amount;
        
        emit Staked(msg.sender, amount, block.timestamp);
    }
    
    /**
     * @dev Unstake RAT tokens
     * @param amount Amount of RAT tokens to unstake
     */
    function unstake(uint256 amount) external nonReentrant updateReward(msg.sender) {
        require(amount > 0, "Amount must be greater than 0");
        require(stakes[msg.sender].amount >= amount, "Insufficient staked amount");
        
        uint256 penalty = 0;
        uint256 stakingDuration = block.timestamp - stakes[msg.sender].stakingTime;
        
        // Apply early unstaking penalty if staked for less than minimum duration
        if (stakingDuration < MIN_STAKE_DURATION) {
            penalty = (amount * EARLY_UNSTAKE_PENALTY) / 10000;
        }
        
        uint256 amountToReturn = amount - penalty;
        
        stakes[msg.sender].amount -= amount;
        poolInfo.totalStaked -= amount;
        
        // Transfer tokens back to user
        ratToken.safeTransfer(msg.sender, amountToReturn);
        
        // If there's a penalty, keep it in the contract (or send to treasury)
        if (penalty > 0) {
            // Penalty stays in contract and can be used for additional rewards
            poolInfo.totalRewards += penalty;
        }
        
        emit Unstaked(msg.sender, amountToReturn, penalty);
    }
    
    /**
     * @dev Claim accumulated rewards
     */
    function claimRewards() external nonReentrant updateReward(msg.sender) {
        uint256 reward = stakes[msg.sender].accumulatedRewards;
        require(reward > 0, "No rewards to claim");
        
        stakes[msg.sender].accumulatedRewards = 0;
        stakes[msg.sender].lastRewardClaim = block.timestamp;
        
        rewardToken.safeTransfer(msg.sender, reward);
        
        emit RewardsClaimed(msg.sender, reward);
    }
    
    /**
     * @dev Add yield to the pool (called by LiquidityAggregator)
     * @param amount Amount of USDT to add as yield
     */
    function addYield(uint256 amount) external {
        require(amount > 0, "Amount must be greater than 0");
        
        rewardToken.safeTransferFrom(msg.sender, address(this), amount);
        poolInfo.totalRewards += amount;
        
        // Increase reward rate based on the yield added
        if (poolInfo.totalStaked > 0) {
            uint256 additionalRate = amount / (7 days); // Distribute over 7 days
            poolInfo.rewardRate += additionalRate;
        }
        
        emit YieldAdded(amount, msg.sender);
    }
    
    /**
     * @dev Set reward rate (admin only)
     * @param _rewardRate New reward rate (rewards per second)
     */
    function setRewardRate(uint256 _rewardRate) external onlyOwner updateReward(address(0)) {
        poolInfo.rewardRate = _rewardRate;
        emit RewardRateUpdated(_rewardRate);
    }
    
    /**
     * @dev Calculate reward per token
     */
    function rewardPerToken() public view returns (uint256) {
        if (poolInfo.totalStaked == 0) {
            return poolInfo.rewardPerTokenStored;
        }
        
        return poolInfo.rewardPerTokenStored + 
            (((block.timestamp - poolInfo.lastUpdateTime) * poolInfo.rewardRate * 1e18) / poolInfo.totalStaked);
    }
    
    /**
     * @dev Calculate earned rewards for an account
     * @param account Address of the account
     */
    function earned(address account) public view returns (uint256) {
        return (stakes[account].amount * 
            (rewardPerToken() - userRewardPerTokenPaid[account]) / 1e18) + 
            stakes[account].accumulatedRewards;
    }
    
    /**
     * @dev Get staking information for an account
     * @param account Address of the account
     */
    function getStakeInfo(address account) external view returns (
        uint256 stakedAmount,
        uint256 stakingTime,
        uint256 stakingDuration,
        uint256 earnedRewards,
        bool canUnstakeWithoutPenalty
    ) {
        StakeInfo memory stakeInfo = stakes[account];
        uint256 duration = block.timestamp - stakeInfo.stakingTime;
        bool noPenalty = duration >= MIN_STAKE_DURATION;
        
        return (
            stakeInfo.amount,
            stakeInfo.stakingTime,
            duration,
            earned(account),
            noPenalty
        );
    }
    
    /**
     * @dev Get pool statistics
     */
    function getPoolStats() external view returns (
        uint256 totalStaked,
        uint256 totalRewards,
        uint256 currentRewardRate,
        uint256 totalStakers
    ) {
        // Note: totalStakers would require additional tracking
        return (
            poolInfo.totalStaked,
            poolInfo.totalRewards,
            poolInfo.rewardRate,
            0 // Placeholder for total stakers
        );
    }
    
    /**
     * @dev Calculate early unstaking penalty for an amount
     * @param account Address of the account
     * @param amount Amount to potentially unstake
     */
    function calculatePenalty(address account, uint256 amount) external view returns (uint256) {
        uint256 stakingDuration = block.timestamp - stakes[account].stakingTime;
        
        if (stakingDuration >= MIN_STAKE_DURATION) {
            return 0;
        }
        
        return (amount * EARLY_UNSTAKE_PENALTY) / 10000;
    }
    
    /**
     * @dev Pause the contract (admin only)
     */
    function pause() external onlyOwner {
        _pause();
    }
    
    /**
     * @dev Unpause the contract (admin only)
     */
    function unpause() external onlyOwner {
        _unpause();
    }
    
    /**
     * @dev Emergency withdrawal function (admin only)
     * @param token Token to withdraw
     * @param amount Amount to withdraw
     */
    function emergencyWithdraw(address token, uint256 amount) external onlyOwner {
        IERC20Extended(token).safeTransfer(owner(), amount);
    }
}

contracts/RATToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

contract RATToken is ERC20, Ownable, Pausable {
    uint256 public constant MAX_SUPPLY = 1000000000 * 10**18; // 1 billion tokens
    uint256 public totalMinted;
    
    // Events
    event TokensMinted(address indexed to, uint256 amount, string reason);
    event TokensBurned(address indexed from, uint256 amount, string reason);
    
    constructor(address initialOwner) 
        ERC20("Real Asset Token", "RAT") 
        Ownable(initialOwner)
    {
        // Mint initial supply to deployer
        uint256 initialSupply = 100000000 * 10**18; // 100 million tokens
        _mint(initialOwner, initialSupply);
        totalMinted = initialSupply;
        
        emit TokensMinted(initialOwner, initialSupply, "Initial mint");
    }
    
    /**
     * @dev Mint new tokens (only owner can call)
     * @param to Address to mint tokens to
     * @param amount Amount of tokens to mint
     * @param reason Reason for minting
     */
    function mint(address to, uint256 amount, string memory reason) external onlyOwner {
        require(to != address(0), "Cannot mint to zero address");
        require(amount > 0, "Amount must be greater than 0");
        require(totalMinted + amount <= MAX_SUPPLY, "Would exceed max supply");
        
        _mint(to, amount);
        totalMinted += amount;
        
        emit TokensMinted(to, amount, reason);
    }
    
    /**
     * @dev Burn tokens from caller's balance
     * @param amount Amount of tokens to burn
     * @param reason Reason for burning
     */
    function burn(uint256 amount, string memory reason) external {
        require(amount > 0, "Amount must be greater than 0");
        require(balanceOf(msg.sender) >= amount, "Insufficient balance");
        
        _burn(msg.sender, amount);
        totalMinted -= amount;
        
        emit TokensBurned(msg.sender, amount, reason);
    }
    
    /**
     * @dev Override transfer to add pause functionality
     */
    function _update(address from, address to, uint256 value) 
        internal 
        whenNotPaused 
        override
    {
        super._update(from, to, value);
    }
    
    /**
     * @dev Pause token transfers (only owner)
     */
    function pause() external onlyOwner {
        _pause();
    }
    
    /**
     * @dev Unpause token transfers (only owner)
     */
    function unpause() external onlyOwner {
        _unpause();
    }
    
    /**
     * @dev Get remaining mintable supply
     */
    function getRemainingMintableSupply() external view returns (uint256) {
        return MAX_SUPPLY - totalMinted;
    }
}

